n = int(input())
if n < 2:
    print('No')
else:
    is_prime = True
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            is_prime = False
            break
    print('Yes' if is_prime else 'No')

#include <iostream>
using namespace std;
int main() {
    long long n;
    if (!(cin >> n)) return 0;
    int count = 0;
    while (n > 0) {
        n = n & (n - 1);
        count++;
    }
    cout << count;
    return 0;
}

import java.util.Scanner;
public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        if(sc.hasNext()) {
            String s = sc.next();
            String rev = new StringBuilder(s).reverse().toString();
            if(s.equals(rev)) System.out.print("Yes");
            else System.out.print("No");
        }
    }
